class ViewAssessment {

    @Service("OboBuildingAssessmentService")
    def assmtSvc;

    def params;
    def list;

    def listHandler = [
        fetchList: { o->
            return list;
        }
    ] as BasicListModel;

    void execute() {
        list = assmtSvc.assess( params );
    }

    def doClose() {
        return "_close";
    }
}
